    private void btnSacuvajActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSacuvajActionPerformed
        if (!validacijaUnos()) {
            return;
        }

        int igracId = Integer.valueOf(txtIgrac.getText().trim());
        int timId = Integer.valueOf(txtTim.getText().trim());
        String pretodnaSezona = txtPrethodnaSezona.getText().trim();
        String projekcija = txtProjekcije.getText().trim();
        int pocetnaCena = Integer.valueOf(txtPocetnaCena.getText().trim());

        Kontroler.getInstance().unesiStatistiku(igracId, timId, pretodnaSezona, projekcija, pocetnaCena);
        JOptionPane.showMessageDialog(this, "Podaci su uspešno uneti!", "Sistemska poruka", JOptionPane.INFORMATION_MESSAGE);

        txtIgrac.setEditable(true);
        txtIgrac.grabFocus();
        txtTim.setEditable(true);
        txtPrethodnaSezona.setEditable(false);
        txtProjekcije.setEditable(false);
        txtPocetnaCena.setEditable(false);
        btnSacuvaj.setEnabled(false);
        btnVrati.setEnabled(false);
        btnProveri.setEnabled(true);
        ocistiPolja();
    }//GEN-LAST:event_btnSacuvajActionPerformed

    private void btnProveriActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnProveriActionPerformed
        if (!validacijaProvera()) {
            return;
        }

        int igracId = Integer.valueOf(txtIgrac.getText().trim());
        int timId = Integer.valueOf(txtTim.getText().trim());

        Igrac i = Kontroler.getInstance().proveriIgraca(igracId, timId);

        if (i != null) {
            lblIgrac.setText(i.toString());
            txtPrethodnaSezona.setEditable(true);
            txtPrethodnaSezona.grabFocus();
            txtProjekcije.setEditable(true);
            txtPocetnaCena.setEditable(true);
            btnSacuvaj.setEnabled(true);
            btnVrati.setEnabled(true);
            txtIgrac.setEditable(false);
            txtTim.setEditable(false);
            btnProveri.setEnabled(false);
        } else {
            JOptionPane.showMessageDialog(this, "Traženi igrač ne postoji u bazi podataka!", "Sistemska poruka", JOptionPane.ERROR_MESSAGE);
            lblIgrac.setText("");
            txtPrethodnaSezona.setEditable(false);
            txtProjekcije.setEditable(false);
            txtPocetnaCena.setEditable(false);
            btnSacuvaj.setEnabled(false);
            btnVrati.setEnabled(false);
            txtIgrac.grabFocus();
        }

        try {
            URI uri = new URI("https://www.google.rs/search?q=" + i.getIme() + "+" + i.getPrezime() + "+rotogrinders");
            Desktop.getDesktop().browse(uri);
        } catch (URISyntaxException urisex) {
            urisex.printStackTrace();
        } catch (IOException ioex) {
            ioex.printStackTrace();
        }
    }//GEN-LAST:event_btnProveriActionPerformed

    private void btnVratiActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnVratiActionPerformed
        ocistiPolja();
        txtIgrac.setEditable(true);
        txtTim.setEditable(true);
        txtPrethodnaSezona.setEditable(false);
        txtProjekcije.setEditable(false);
        txtPocetnaCena.setEditable(false);
        btnSacuvaj.setEnabled(false);
        btnVrati.setEnabled(false);
        btnProveri.setEnabled(true);
        txtIgrac.grabFocus();
    }//GEN-LAST:event_btnVratiActionPerformed

