    private boolean validacijaProvera() {
        try {
            if (!txtIgrac.getText().equals("") && !txtIgrac.getText().equals("")) {
                int igracId = Integer.valueOf(txtIgrac.getText().trim());
                int timId = Integer.valueOf(txtTim.getText().trim());
                return true;
            }
            JOptionPane.showMessageDialog(this, "Niste uneli sve potrebne parametre!", "Sistemska poruka", JOptionPane.ERROR_MESSAGE);
            return false;
        } catch (NumberFormatException nfex) {
            JOptionPane.showMessageDialog(this, "Niste uneli broj!", "Sistemska poruka", JOptionPane.ERROR_MESSAGE);
            nfex.printStackTrace();
            return false;
        }
    }

    private boolean validacijaUnos() {
        try {
            if (!txtPocetnaCena.getText().equals("")) {
                int pocetnaCena = Integer.valueOf(txtPocetnaCena.getText().trim());
                return true;
            }
            JOptionPane.showMessageDialog(this, "Niste uneli sve potrebne parametre!", "Sistemska poruka", JOptionPane.ERROR_MESSAGE);
            return false;
        } catch (NumberFormatException nfex) {
            JOptionPane.showMessageDialog(this, "Niste uneli broj!", "Sistemska poruka", JOptionPane.ERROR_MESSAGE);
            nfex.printStackTrace();
            return false;
        }
    }

    private void ocistiPolja() {
        txtIgrac.setFocusable(true);
        txtPrethodnaSezona.setText("");
        txtProjekcije.setText("");
        txtPocetnaCena.setText("");
        lblIgrac.setText("");
    }

    private void inicijalizujFormu() {
        this.setLocationRelativeTo(null);
        this.setTitle("Unos statistike za igrače - Fantasy NBA");
        this.setAlwaysOnTop(true);
        this.setResizable(false);
        txtIgrac.grabFocus();
    }

